// Pattern: Hash Map Frequency Count
// Time: O(n) | Space: O(1)
func isAnagram(s, t string) bool {
    if len(s) != len(t) { return false }
    freq := [26]int{}
    for i := 0; i < len(s); i++ {
        freq[s[i]-'a']++
        freq[t[i]-'a']--
    }
    for _, count := range freq {
        if count != 0 { return false }
    }
    return true
}
